// client/src/Login.js
import React, { useState } from 'react';
import axios from './api'; // Ensure this points to your Axios instance

function Login() {
    const [username, setUsername] = useState('');
    const [dynamicPassword, setDynamicPassword] = useState('');
    const [uniqueValue, setUniqueValue] = useState('');
    const [fixedValue, setFixedValue] = useState('');
    const [trigFunction, setTrigFunction] = useState('sin');
    const p="45"
    const calculateDynamicPassword = () => {
        const currentHour = new Date().getHours();
        const digitSum = p.split('').reduce((sum, char) => sum + parseInt(char.charCodeAt(0) - 96 || 0, 10), 0); // Sum of ASCII values

        // Calculate the angle based on the current hour and fixed value
        const angle = currentHour - parseFloat(fixedValue);

        // Calculate the trigonometric result based on the selected function
        let trigResult;
        switch (trigFunction) {
            case 'sin':
                trigResult = Math.sin(angle);
                break;
            case 'cos':
                trigResult = Math.cos(angle);
                break;
            case 'tan':
                trigResult = Math.tan(angle);
                break;
            default:
                trigResult = Math.sin(angle); // Default to sin
        }

        // Calculate the final dynamic password
        const calculatedPassword = (digitSum * parseFloat(uniqueValue) * Math.sin(parseFloat(fixedValue)) * trigResult).toFixed(3);
        setDynamicPassword(calculatedPassword); // Set the dynamic password in state
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        const calculatedDynamicPassword = dynamicPassword; // Use the password already set
        try {
            const res = await axios.post('/auth/login', {
                username,
                dynamicPassword: calculatedDynamicPassword
            });
            alert('Login successful!');
        } catch (error) {
            console.error(error);
            alert('Login failed: ' + error.response.data.error);
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Username"
                    required
                />
                <input
                    type="number"
                    value={uniqueValue}
                    onChange={(e) => setUniqueValue(e.target.value)}
                    placeholder="Unique Value"
                    required
                />
                <input
                    type="number"
                    value={fixedValue}
                    onChange={(e) => setFixedValue(e.target.value)}
                    placeholder="Fixed Value"
                    required
                />
                <select value={trigFunction} onChange={(e) => setTrigFunction(e.target.value)}>
                    <option value="sin">Sin</option>
                    <option value="cos">Cos</option>
                    <option value="tan">Tan</option>
                </select>
                <input
                    type="text"
                    value={dynamicPassword}
                    readOnly
                    placeholder="Dynamic Password"
                />
                <button type="button" onClick={calculateDynamicPassword}>
                    Calculate Dynamic Password
                </button>
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
