// client/src/Login.js
import React, { useState } from 'react';
import axios from './api';

function Login() {
    const [username, setUsername] = useState('');
    const [dynamicPassword, setDynamicPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('/auth/login', { username, password: dynamicPassword });
            setDynamicPassword(res.data.dynamicPassword); // Display dynamic password to user
        } catch (error) {
            console.error(error);
            alert('Login failed');
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" required />
                <input type="text" value={dynamicPassword} onChange={(e) => setDynamicPassword(e.target.value)} placeholder="Dynamic Password" required />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
