import React, { useState } from 'react';
import axios from './api';

function Login() {
    const [username, setUsername] = useState('');
    const [uniqueValue, setUniqueValue] = useState('');
    const [fixedValue, setFixedValue] = useState('');
    const [keyValue, setKeyValue] = useState('');
    const [dynamicPassword, setDynamicPassword] = useState('');

    // Function to calculate dynamic password
    const calculateDynamicPassword = () => {
        const hour = new Date().getHours(); // Get the current hour
        const fixedNum = parseFloat(fixedValue);
        const uniqueNum = parseFloat(uniqueValue);
        const keyNum = parseFloat(keyValue);

        let trigFunctionValue = Math.sin(fixedNum); // Default to sin, adjust based on your logic
        
        // Implement logic to choose trig function
        // e.g., if a dropdown or state to choose between sin, cos, or tan

        const calculatedPassword = (username.length * uniqueNum * trigFunctionValue * (hour - keyNum)).toFixed(2);
        setDynamicPassword(calculatedPassword); // Set the dynamic password based on calculation
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('/auth/login', { username, password: dynamicPassword });
            // Handle successful login
            alert('Login successful!');
        } catch (error) {
            console.error(error);
            alert('Login failed: ' + error.response.data.error);
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Username"
                    required
                />
                <input
                    type="number"
                    value={uniqueValue}
                    onChange={(e) => setUniqueValue(e.target.value)}
                    placeholder="Unique Value"
                    required
                />
                <input
                    type="number"
                    value={fixedValue}
                    onChange={(e) => setFixedValue(e.target.value)}
                    placeholder="Fixed Value"
                    required
                />
                <input
                    type="number"
                    value={keyValue}
                    onChange={(e) => setKeyValue(e.target.value)}
                    placeholder="Key Value"
                    required
                />
                <button type="button" onClick={calculateDynamicPassword}>
                    Calculate Dynamic Password
                </button>
                <input
                    type="text"
                    value={dynamicPassword}
                    readOnly // make it read-only
                    placeholder="Dynamic Password"
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
