import React, { useState } from 'react';
import axios from './api';
import generateDynamicPassword from '../src/'; // Import the function

function Login() {
    const [username, setUsername] = useState('');
    const [uniqueValue, setUniqueValue] = useState('');
    const [fixedValue, setFixedValue] = useState('');
    const [keyValue, setKeyValue] = useState('');
    const [dynamicPassword, setDynamicPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        // Get current hour
        const currentHour = new Date().getHours();

        // Calculate the dynamic password using the utility function
        const calculatedPassword = generateDynamicPassword(
            username,
            uniqueValue,
            fixedValue,
            currentHour,
            keyValue,
            'sin' // You can replace 'sin' with the chosen trig function
        );

        try {
            const res = await axios.post('/auth/login', { username, password: calculatedPassword });
            // Handle successful login
            alert('Login successful!');
        } catch (error) {
            console.error(error);
            alert('Login failed: ' + error.response.data.error);
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Username"
                    required
                />
                <input
                    type="number"
                    value={uniqueValue}
                    onChange={(e) => setUniqueValue(e.target.value)}
                    placeholder="Unique Value"
                    required
                />
                <input
                    type="number"
                    value={fixedValue}
                    onChange={(e) => setFixedValue(e.target.value)}
                    placeholder="Fixed Value"
                    required
                />
                <input
                    type="number"
                    value={keyValue}
                    onChange={(e) => setKeyValue(e.target.value)}
                    placeholder="Key Value"
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
