// client/src/Register.js
import React, { useState } from 'react';
import axios from './api';

function Register() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [uniqueValue, setUniqueValue] = useState('');
    const [fixedValue, setFixedValue] = useState(''); // This will still be input by user
    const [trigFunction, setTrigFunction] = useState('sin'); // Default to 'sin'
    const [keyValue, setKeyValue] = useState(''); // Numeric key value input

    const handleRegister = async (e) => {
        e.preventDefault();

        // Convert fixed value to a number
        const fixedNum = parseFloat(fixedValue);
        let key;

        // Calculate the key based on the selected trigonometric function
        switch (trigFunction) {
            case 'sin':
                key = Math.sin(fixedNum); // Always using fixed value for sin
                break;
            case 'cos':
                key = Math.cos(fixedNum);
                break;
            case 'tan':
                key = Math.tan(fixedNum);
                break;
            default:
                key = Math.sin(fixedNum); // Fallback to sin if something goes wrong
        }

        // Combine the key value with the calculated key
        const finalKey = key + parseFloat(keyValue); // Modify how you want to combine this

        try {
            await axios.post('/auth/register', { username, password, uniqueValue, finalKey });
            alert('User registered successfully');
        } catch (error) {
            console.error(error);
            alert('Registration failed');
        }
    };

    return (
        <div>
            <form onSubmit={handleRegister}>
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" required />
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
                <input type="number" value={uniqueValue} onChange={(e) => setUniqueValue(e.target.value)} placeholder="Unique Value" required />
                <input type="number" value={fixedValue} onChange={(e) => setFixedValue(e.target.value)} placeholder="Fixed Value" required />
                <select value={trigFunction} onChange={(e) => setTrigFunction(e.target.value)}>
                    <option value="sin">Sine (default)</option>
                    <option value="cos">Cosine</option>
                    <option value="tan">Tangent</option>
                </select>
                <input type="number" value={keyValue} onChange={(e) => setKeyValue(e.target.value)} placeholder="Numeric Key Value" required />
                <button type="submit">Register</button>
            </form>
        </div>
    );
}

export default Register;
