import { initializeApp } from "firebase/app";
import { getFirestore,collection,getDocs, addDoc,deleteDoc,doc, onSnapshot,query,where,updateDoc,orderBy,limit} from "firebase/firestore";

const firebaseConfig = {
  apiKey: "AIzaSyDnqFstg7haG60B6vUWbn7hvAFFs5fjSZw",
  authDomain: "fir-d784c.firebaseapp.com",
  projectId: "fir-d784c",
  storageBucket: "fir-d784c.appspot.com",
  messagingSenderId: "1081359533358",
  appId: "1:1081359533358:web:50c99e54ab365991ebe6bf"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const db = getFirestore();
const colref = collection(db, 'users');

document.addEventListener('DOMContentLoaded', () => {
const form = document.getElementById('dataForm');
if(form)
form.addEventListener('submit', async (e) => {
    e.preventDefault();
    const name = document.getElementById('name').value;
    const age = document.getElementById('age').value;

    await db.collection('users').add({ name, age });
    alert('Data added successfully!');
    form.reset();
});

});
